mapper.select:
select *
from mainuser
where email = #{email} and password = #{password}

select password
from mainuser
where user_idx = #{userIdx}

select count(nickname)
from mainuser
where nickname =#{nickname}

mapper.update:
update mainuser
set refresh_token = #{refreshToken}
where user_idx = #{userIdx}

update mainuser
set email=#{email}, nickname = #{nickname}, emote_color_idx = #{emoteColor}
where user_idx = #{userIdx}

update mainuser
set password=#{password}
where user_idx = #{userIdx}

update mainuser
set is_deleted = 1, update_date = now()
where user_idx = #{userIdx}

update mainuser
set refresh_token=null
where user_idx = #{userIdx}

mapper.select:
select user_idx
from mainuser
where email = #{email}
